type Device struct {
	buffer string
}

func makeDevice(buffer string) Device {
	return Device{buffer: buffer}
}

func (d Device) findStartMarker(spanSize int) int {
OuterLoop:
	for i := range d.buffer {
		if i < spanSize {
			continue
		}
		for j := 0; j < spanSize; j++ { // for each of the last 4 letters, compare to all other letters
			char := d.buffer[i-j]
			for k := 1; k < spanSize-j; k++ {
				if char == d.buffer[i-j-k] {
					continue OuterLoop
				}
			}
		}
		return i + 1
	}
	return 0
}
